cells.data:
#container {
  border: 2px solid #ff0000;
  width: 800px;
}
footer {
  width: 100%;
}
footer div {
  width: 33.3333333%;
  background: rgba(113, 15, 154, 0.73);
  float: left;
  vertical-align: top;
  overflow-x: none;
  text-align: center;
}
footer div section {
  max-width: 100%;
  padding: 0;
  text-align: inherit;
}
footer div img {
  width: 60%;
}
